CREATE TABLE Vehicle (\n    VehicleID INT PRIMARY KEY,\n    Make VARCHAR(50),\n    Model VARCHAR(50),\n    Year INT\n);\n\nCREATE TABLE Car (\n    CarID INT PRIMARY KEY,\n    VehicleID INT NOT NULL,\n    NumDoors INT,\n    FOREIGN KEY (VehicleID) REFERENCES Vehicle(VehicleID)\n);\n\nCREATE TABLE Truck (\n    TruckID INT PRIMARY KEY,\n    VehicleID INT NOT NULL,\n    Capacity INT,\n    FOREIGN KEY (VehicleID) REFERENCES Vehicle(VehicleID)\n);