CREATE TABLE questions_meta (\n  id SERIAL PRIMARY KEY,\n  title VARCHAR(255),\n  content TEXT,\n  created_at TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP\n);\n\nCREATE TABLE questions_stats (\n  id SERIAL PRIMARY KEY,\n  question_id INTEGER NOT NULL REFERENCES questions_meta(id),\n  recently_viewed_at TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,\n  total_views INTEGER NOT NULL DEFAULT 0\n);