CREATE TABLE products (\n  id INT PRIMARY KEY,\n  name VARCHAR(255) NOT NULL\n);\n\nCREATE TABLE product_groups (\n  id INT PRIMARY KEY,\n  name VARCHAR(255) NOT NULL\n);\n\nCREATE TABLE attributes (\n  id INT PRIMARY KEY,\n  product_group_id INT NOT NULL,\n  name VARCHAR(255) NOT NULL,\n  data_type VARCHAR(20) NOT NULL,  -- e.g., 'int', 'varchar', 'datetime'\n  FOREIGN KEY (product_group_id) REFERENCES product_groups (id)\n);\n\nCREATE TABLE attribute_values_int (\n  attribute_id INT NOT NULL,\n  product_id INT NOT NULL,\n  value INT NOT NULL,\n  PRIMARY KEY (attribute_id, product_id),\n  FOREIGN KEY (attribute_id) REFERENCES attributes (id),\n  FOREIGN KEY (product_id) REFERENCES products (id)\n);\n\nCREATE TABLE attribute_values_varchar (\n  attribute_id INT NOT NULL,\n  product_id INT NOT NULL,\n  value VARCHAR(255) NOT NULL,\n  PRIMARY KEY (attribute_id, product_id),\n  FOREIGN KEY (attribute_id) REFERENCES attributes (id),\n  FOREIGN KEY (product_id) REFERENCES products (id)\n);\n\n-- Add more tables for other data types as needed