CREATE TABLE product_exclusion_matrix (\n  product_code VARCHAR(10),\n  allowed_product_code VARCHAR(10)\n);\n\nINSERT INTO product_exclusion_matrix (product_code, allowed_product_code)\nVALUES\n  ('MRLSPN', 'LGP'),\n  ('MRLSDF', 'LGP'),\n  ('LGS', 'LGP'),\n  ('LGP', 'MRLSPN'),\n  ('LGP', 'MRLSDF'),\n  ('HOBN', 'LGS'),\n  ('HOBN', 'LGP'),\n  ('HODF', 'LGS'),\n  ('HODF', 'LGP'),\n  ('HVO', 'MRLSPN'),\n  ('HVO', 'MRLSDF'),\n  ('HVO', 'LGS'),\n  ('HVO', 'LGP'),\n  ('HVO', 'HOBN'),\n  ('HVO', 'HODF'),\n  ('HVOF', 'MRLSPN'),\n  ('HVOF', 'MRLSDF'),\n  ('HVOF', 'LGS'),\n  ('HVOF', 'LGP'),\n  ('HVOF', 'HOBN'),\n  ('HVOF', 'HODF');\n\nSELECT pem.allowed_product_code\nFROM product_exclusion_matrix pem\nWHERE pem.product_code = 'HVO';