CREATE PROCEDURE SAFE_DROP_TABLE\n    @TableName NVARCHAR(128)\nAS\nBEGIN\n    IF EXISTS (SELECT * FROM sys.tables WHERE name = @TableName)\n    BEGIN\n        BEGIN TRY\n            DECLARE @sql NVARCHAR(200) = 'DROP TABLE ' + QUOTENAME(@TableName);\n            EXEC sp_executesql @sql;\n            PRINT 'Dropped table ' + @TableName;\n        END TRY\n        BEGIN CATCH\n            PRINT 'Failed to drop ' + @TableName + '. Error: ' + ERROR_MESSAGE();\n        END CATCH\n    END\n    ELSE\n    BEGIN\n        PRINT 'No action needed for table ' + @TableName;\n    END\nEND\nGO