CREATE FUNCTION dbo.GenerateRandomText (@length INT)\nRETURNS VARCHAR(MAX)\nAS\nBEGIN\n    DECLARE @text VARCHAR(MAX) = ''\n    DECLARE @word VARCHAR(50)\n    DECLARE @words TABLE (word VARCHAR(50))\n\n    -- add some random words to the table\n    INSERT INTO @words (word) \n    VALUES ('Lorem'), ('ipsum'), ('dolor'), ('sit'), ('amet'), ('consectetur'), \n           ('adipiscing'), ('elit'), ('sed'), ('do'), ('eiusmod'), ('tempor'), \n           ('incididunt'), ('ut'), ('labore'), ('et'), ('dolore'), ('magna'), \n           ('aliqua'), ('enim'), ('ad'), ('minim'), ('veniam'), ('quis'), \n           ('nostrud'), ('exercitation'), ('ullamco'), ('laboris'), ('nisi'), \n           ('ut'), ('aliquip'), ('ex'), ('ea'), ('commodo'), ('consequat'), \n           ('duis'), ('aute'), ('irure'), ('dolor'), ('in'), ('reprehenderit'), \n           ('in'), ('voluptate'), ('velit'), ('esse'), ('cillum'), ('dolore'), \n           ('eu'), ('fugiat'), ('nulla'), ('pariatur'), ('excepteur'), ('sint'), \n           ('occaecat'), ('cupidatat'), ('non'), ('proident'), ('sunt'), ('in'), \n           ('culpa'), ('qui'), ('officia'), ('deserunt'), ('mollit'), ('anim'), \n           ('id'), ('est'), ('laborum')\n\n    WHILE LEN(@text) < @length\n    BEGIN\n        SELECT TOP 1 @word = word FROM @words ORDER BY NEWID()\n        SET @text += ' ' + @word\n    END\n\n    RETURN LTRIM(RTRIM(@text))\nEND\nGO\n\n-- example usage:\nSELECT dbo.GenerateRandomText(200) AS RandomText