CREATE TABLE users (\n  id INT PRIMARY KEY,\n  name VARCHAR(255)\n);\n\nCREATE TABLE orders (\n  id INT PRIMARY KEY,\n  user_id INT,\n  FOREIGN KEY (user_id) REFERENCES users(id)\n);\n\nCREATE TABLE order_items (\n  id INT PRIMARY KEY,\n  order_id INT,\n  product_id INT,\n  FOREIGN KEY (order_id) REFERENCES orders(id)\n);\n\nCREATE TABLE products (\n  id INT PRIMARY KEY,\n  order_item_id INT,\n  FOREIGN KEY (order_item_id) REFERENCES order_items(id)\n);