DELIMITER //\nCREATE PROCEDURE prefix_columns()\nBEGIN\n  DECLARE sql VARCHAR(255);\n  SET sql = 'SELECT a.*, ';\n  SET sql = CONCAT(sql, (SELECT GROUP_CONCAT(COLUMN_NAME) FROM INFORMATION_SCHEMA.COLUMNS WHERE TABLE_NAME = 'TABLE_B' AND TABLE_SCHEMA = 'your_database'));\n  SET sql = CONCAT(sql, ' AS b_');\n  SET sql = CONCAT(sql, (SELECT GROUP_CONCAT(COLUMN_NAME) FROM INFORMATION_SCHEMA.COLUMNS WHERE TABLE_NAME = 'TABLE_B' AND TABLE_SCHEMA = 'your_database'));\n  SET sql = CONCAT(sql, ' FROM TABLE_A a JOIN TABLE_B b USING (some_id)');\n  PREPARE stmt FROM sql;\n  EXECUTE stmt;\n  DEALLOCATE PREPARE stmt;\nEND//\nDELIMITER ;