CREATE EXTENSION oracle_fdw;\n\nCREATE SERVER oracle_server FOREIGN DATA WRAPPER oracle_fdw\n  OPTIONS (dbserver '//oracle_host:1521/orcl');\n\nCREATE USER MAPPING FOR CURRENT_USER SERVER oracle_server\n  OPTIONS (username 'oracle_user', password 'oracle_password');\n\nCREATE FOREIGN TABLE oracle_table (\n  id integer,\n  name varchar(50)\n)\nSERVER oracle_server\nOPTIONS (table 'oracle_schema.oracle_table');