DECLARE\n  xml_file BFILE;\n  xml_data XMLType;\nBEGIN\n  xml_file := BFILENAME('XML_DIR', 'badges.xml');\n  xml_data := XMLType(xml_file, NULL, XMLType.NAMESPACE);\n  INSERT INTO badges_table (userid, name, date)\n  SELECT *\n  FROM XMLTable('/badges/row'\n                PASSING xml_data\n                COLUMNS userid  NUMBER PATH '@UserId',\n                        name    VARCHAR2(20) PATH '@Name',\n                        date    DATE PATH '@Date');\nEND;\n/