MERGE INTO YourTable AS target\nUSING (SELECT @ScorerID AS ScorerID, @Score AS Score, CAST(GETDATE() AS date) AS DateCreated) AS source\nON target.ScorerID = source.ScorerID AND CAST(target.DateCreated AS date) = source.DateCreated\nWHEN MATCHED THEN\n    UPDATE SET target.Score = source.Score\nWHEN NOT MATCHED THEN\n    INSERT (ScorerID, Score, DateCreated)\n    VALUES (source.ScorerID, source.Score, source.DateCreated);