SELECT \n    NEWVALUE = STUFF((SELECT ', ' + VALUE \n                      FROM #yourTable t2 \n                      WHERE t2.GROUP = t1.GROUP AND t2.ID IN (3, 4)\n                      FOR XML PATH(''), TYPE).value('.', 'nvarchar(max)'), 1, 2, '') + \n               ' (' + (SELECT VALUE FROM #yourTable t3 WHERE t3.GROUP = t1.GROUP AND t3.ID = 5) + ')',\n    GROUP\nFROM #yourTable t1\nWHERE ID = 4\nGROUP BY GROUP