DECLARE @sql nvarchar(max) = 'SELECT * FROM tProject WHERE 1=0';\nDECLARE @columnName nvarchar(100);\n\nDECLARE cur CURSOR FOR \n    SELECT name FROM sys.columns WHERE object_id = OBJECT_ID('tProject') AND system_type_id IN (167, 231); -- nvarchar and ntext\n\nOPEN cur;\n\nFETCH NEXT FROM cur INTO @columnName;\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    SET @sql += ' OR CONVERT(nvarchar(max), ' + QUOTENAME(@columnName) + ') LIKE ''%searchTerm%''';\n    FETCH NEXT FROM cur INTO @columnName;\nEND\n\nCLOSE cur;\nDEALLOCATE cur;\n\nEXEC sp_executesql @sql;