SELECT *\nFROM tProject\nWHERE \n    CONVERT(nvarchar(max), name) LIKE '%searchTerm%' OR\n    CONVERT(nvarchar(max), Goal) LIKE '%searchTerm%' OR\n    CONVERT(nvarchar(max), Activities) LIKE '%searchTerm%' OR\n    CONVERT(nvarchar(max), Result) LIKE '%searchTerm%' OR\n    CONVERT(nvarchar(max), MonName) LIKE '%searchTerm%' OR\n    CONVERT(nvarchar(max), MonGoal) LIKE '%searchTerm%' OR\n    CONVERT(nvarchar(max), MonActivities) LIKE '%searchTerm%' OR\n    CONVERT(nvarchar(max), MonResult) LIKE '%searchTerm%' OR\n    CONVERT(nvarchar(max), TotalFund) LIKE '%searchTerm%';