SELECT TimeStamp, \n       CASE \n           WHEN Attribute = 'SpeedMax' THEN Speed\n           WHEN Attribute = 'SpeedMin' THEN Speed\n           ELSE NULL\n       END AS Speed,\n       CASE \n           WHEN Attribute = 'HeightMax' THEN Height\n           WHEN Attribute = 'HeightMin' THEN Height\n           ELSE NULL\n       END AS Height\nFROM (\n    SELECT TimeStamp, SpeedMax, SpeedMin, HeightMax, HeightMin\n    FROM YourTable\n) AS SourceTable\nUNPIVOT (\n    Value FOR Attribute IN (SpeedMax, SpeedMin, HeightMax, HeightMin)\n) AS UnpivotTable