WITH latest_status AS (\n  SELECT \n    itemId, \n    statusKey, \n    statusDate, \n    ROW_NUMBER() OVER (PARTITION BY itemId ORDER BY statusDate DESC) AS row_num\n  FROM \n    todo_itemStatus\n)\nSELECT \n  ti.itemId, \n  ti.title, \n  ls.statusKey, \n  ls.statusDate\nFROM \n  todo_item ti\n  JOIN todo_project tp ON ti.projectId = tp.projectId\n  JOIN latest_status ls ON ti.itemId = ls.itemId\nWHERE \n  tp.projectId = 13\n  AND ls.row_num = 1