CREATE VIEW CandidatesView WITH SCHEMABINDING AS\nSELECT 'Candidate' AS Source, CandidateID AS SourceId, LastName + ', ' + FirstName AS SourceName\nFROM dbo.Candidates;\n\nCREATE UNIQUE CLUSTERED INDEX idx_CandidatesView ON CandidatesView (SourceId);\n\nCREATE VIEW ResourcesView WITH SCHEMABINDING AS\nSELECT 'Resource' AS Source, ResourceID AS SourceId, LastName + ', ' + FirstName AS SourceName\nFROM dbo.Resources;\n\nCREATE UNIQUE CLUSTERED INDEX idx_ResourcesView ON ResourcesView (SourceId);\n\nCREATE VIEW DealsView WITH SCHEMABINDING AS\nSELECT 'Deal' AS Source, DealID AS SourceId, CONVERT(varchar, Number) + '-' + CONVERT(varchar, RevisionNumber) AS SourceName\nFROM dbo.Deals;\n\nCREATE UNIQUE CLUSTERED INDEX idx_DealsView ON DealsView (SourceId);\n\nCREATE VIEW JobOrdersView WITH SCHEMABINDING AS\nSELECT 'Job Order' AS Source, JobOrderID AS SourceId, CustomerNumber AS SourceName\nFROM dbo.JobOrders;\n\nCREATE UNIQUE CLUSTERED INDEX idx_JobOrdersView ON JobOrdersView (SourceId);