CREATE TABLE schema1.EMPLOYEE (\n  empID INT PRIMARY KEY,\n  empName VARCHAR(255),\n  deptId INT\n);\n\nCREATE TABLE schema2.DEPARTMENT (\n  deptId INT PRIMARY KEY,\n  deptName VARCHAR(255)\n);\n\nALTER TABLE schema1.EMPLOYEE\nADD CONSTRAINT fk_deptId FOREIGN KEY (deptId)\nREFERENCES schema2.DEPARTMENT (deptId);