CREATE TABLE Question (\n  question-id INT PRIMARY KEY,\n  -- other columns\n);\n\nCREATE TABLE Question-tag-xref (\n  question-id INT,\n  question-tag VARCHAR(255),\n  PRIMARY KEY (question-id, question-tag),\n  FOREIGN KEY (question-id) REFERENCES Question(question-id)\n);\n\nCREATE TABLE Answers (\n  answer-id INT PRIMARY KEY, -- Assuming this as the primary key\n  question-id INT,\n  answer TEXT,\n  FOREIGN KEY (question-id) REFERENCES Question(question-id)\n);