CREATE TABLE foo AS\nSELECT 10 AS x, 'a' AS y FROM dual UNION\nSELECT 20, 'b' FROM dual UNION\nSELECT 30, 'c' FROM dual;\n\nSELECT y\nFROM (\n  SELECT x, y, LEAD(x, 1, NULL) OVER (ORDER BY x) AS next_x\n  FROM foo\n)\nWHERE x <= 19;  -- returns: a\n\nSELECT y\nFROM (\n  SELECT x, y, LEAD(x, 1, NULL) OVER (ORDER BY x) AS next_x\n  FROM foo\n)\nWHERE x <= 20;  -- returns: b\n\nSELECT y\nFROM (\n  SELECT x, y, LEAD(x, 1, NULL) OVER (ORDER BY x) AS next_x\n  FROM foo\n)\nWHERE x <= 21;  -- returns: b