-- Database schema example\nCREATE TABLE operations (\n  id INT PRIMARY KEY,\n  status VARCHAR(50) NOT NULL,\n  started_at TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,\n  completed_at TIMESTAMP\n);\n\n-- Insert record when operation starts\nINSERT INTO operations (status) VALUES ("started");\n\n-- Update record when operation completes\nUPDATE operations SET status = "completed", completed_at = CURRENT_TIMESTAMP WHERE id = 1;