SELECT \n  (avg_row_size * expected_num_rows) + (block_overhead * num_blocks)\nFROM \n  (SELECT \n     AVG(row_size) AS avg_row_size,\n     SUM(block_overhead) AS block_overhead,\n     COUNT(*) AS num_blocks\n   FROM \n     (SELECT \n        NVL(VSIZE(column1), 0) + NVL(VSIZE(column2), 0) + ... + NVL(VSIZE(columnN), 0) AS row_size,\n        CASE WHEN MOD(rownum, rows_per_block) = 0 THEN block_overhead ELSE 0 END AS block_overhead\n      FROM \n        your_table)\n  );