-- Transaction 1 (IsolationLevel = READ COMMITTED)\nBEGIN TRANSACTION\nUPDATE table1 SET column1 = 'new value'\n-- Acquires an exclusive lock on the updated row\n\n-- Concurrent SELECT statement (outside of the transaction)\nSELECT * FROM table1 WITH (NOLOCK)\n-- Will not be blocked by Transaction 1's lock\n\n-- Concurrent UPDATE statement (outside of the transaction)\nUPDATE table1 SET column2 = 'another value'\n-- Will be blocked by Transaction 1's lock