DECLARE @sql nvarchar(max) = '';\nDECLARE @old_value nvarchar(50) = 'old_value';\nDECLARE @new_value nvarchar(50) = 'new_value';\n\nSELECT @sql += 'UPDATE your_table SET ' + QUOTENAME(name) + ' = REPLACE(' + QUOTENAME(name) + ', ''' + @old_value + ''', ''' + @new_value + ''') '\nFROM sys.columns \nWHERE object_id = OBJECT_ID('your_table') AND system_type_id = 167; -- 167 is the system_type_id for varchar\n\nEXEC sp_executesql @sql;