SELECT \n    t.name AS TableName\nFROM \n    sys.database_permissions perm\nINNER JOIN \n    sys.database_principals p ON perm.grantee_principal_id = p.principal_id\nINNER JOIN \n    sys.tables t ON perm.major_id = t.object_id\nWHERE \n    perm.class = 1 -- Object\n    AND perm.state_desc = 'GRANT'\n    AND perm.permission_name = 'DELETE'\n    AND p.name = '<specific_username>'\n    AND t.is_ms_shipped = 0 -- exclude system tables;