SELECT \n    CASE \n        WHEN Price BETWEEN 50 AND 100 THEN '$50 - $100'\n        WHEN Price BETWEEN 101 AND 500 THEN '$100 - $500'\n        WHEN Price BETWEEN 501 AND 1000 THEN '$500 - $1000'\n        ELSE 'Other'\n    END AS PriceRange,\n    COUNT(*) AS ProductCount\nFROM \n    YourTable\nGROUP BY \n    CASE \n        WHEN Price BETWEEN 50 AND 100 THEN '$50 - $100'\n        WHEN Price BETWEEN 101 AND 500 THEN '$100 - $500'\n        WHEN Price BETWEEN 501 AND 1000 THEN '$500 - $1000'\n        ELSE 'Other'\n    END\nORDER BY \n    PriceRange;