SELECT \n  pl.id, \n  pl.name, \n  i.imageFileLocation \nFROM \n  ProductLines pl \n  LEFT JOIN (\n    SELECT \n      pil.specificProductID, \n      i.imageFileLocation \n    FROM \n      ProductImageLinks pil \n      JOIN Images i ON pil.imageID = i.id \n    ORDER BY RAND() \n    LIMIT 1\n  ) AS random_image ON pl.id = random_image.specificProductID \nWHERE \n  pl.id IN (SELECT id FROM ProductLines);