WITH RandomImage AS (\n  SELECT \n    im.property_id_ref,\n    im.image_file,\n    pm.property_name,\n    ROW_NUMBER() OVER (PARTITION BY im.property_id_ref ORDER BY NEWID()) AS rn\n  FROM \n    property_master pm\n  INNER JOIN \n    image_master im ON pm.property_id = im.property_id_ref\n  INNER JOIN \n    customer_master cm ON pm.customer_id = cm.customer_id\n)\nSELECT TOP (2) \n  property_id_ref,\n  image_file,\n  property_name\nFROM \n  RandomImage\nWHERE \n  rn = 1\nORDER BY \n  property_id_ref;