SELECT \n  fb.auto_id, \n  fb.user_id, \n  fb.bulletin, \n  fb.subject, \n  fb.color, \n  fb.submit_date, \n  fru.disp_name, \n  fru.pic_url\nFROM \n  friend_bulletin AS fb\n  INNER JOIN friend_friend AS ff ON fb.user_id = ff.userid\n  INNER JOIN friend_reg_user AS fru ON fb.user_id = fru.auto_id\nWHERE \n  ff.friendid = $MY_ID AND ff.status = 1\n  OR fb.user_id = $MY_ID\nORDER BY \n  fb.auto_id DESC\nLIMIT 0, 30;