SELECT \n  r.id,\n  r.event_id,\n  r.team,\n  r.status,\n  GROUP_CONCAT(p.id, p.name, p.team SEPARATOR ',') AS people,\n  GROUP_CONCAT(e.id, e.name, e.team SEPARATOR ',') AS entries\nFROM \n  registrations r\n  LEFT JOIN registration_people rp ON r.id = rp.registration_id\n  LEFT JOIN person p ON rp.person_id = p.id\n  LEFT JOIN registration_entries re ON r.id = re.registration_id\n  LEFT JOIN entry e ON re.entry_id = e.id\nWHERE \n  r.event_id = $event_id\nGROUP BY \n  r.id, r.event_id, r.team, r.status