CREATE OR REPLACE TRIGGER "JOSH".TEST#UPD\nBEFORE UPDATE ON "JOSH"."TEST_TRIGGER_TABLE"\nREFERENCING OLD AS OLD NEW AS NEW\nFOR EACH ROW\nDECLARE\n  column_names ora_name_list_t;\n  iterator ora_name_list_iterator_t;\n  column_name VARCHAR2(30);\n  oldval VARCHAR2(2000);\n  newval VARCHAR2(2000);\nBEGIN\n  column_names := ora_get_columns(:old);\n  iterator := column_names.FIRST;\n  WHILE iterator IS NOT NULL LOOP\n    column_name := iterator.COLUMN_NAME;\n    oldval := :old(column_name);\n    newval := :new(column_name);\n    -- Do something here with the old and new values\n    iterator := column_names.NEXT(iterator);\n  END LOOP;\nEND;