-- Create a temporary table with the same structure as your original table\nSELECT TOP 0 * INTO #TempTable\nFROM YourOriginalTable;\n\n-- Insert the rows to be swapped into the temporary table\nINSERT INTO #TempTable (ID, CODE, NAME)\nSELECT 2, 'h1100h1', 'Cool example1'\nUNION ALL\nSELECT 1, 'h654441', 'Another cool1';\n\n-- DELETE the rows from the original table\nDELETE FROM YourOriginalTable\nWHERE ID IN (1, 2);\n\n-- Insert the swapped rows back into the original table\nINSERT INTO YourOriginalTable (ID, CODE, NAME)\nSELECT ID, CODE, NAME\nFROM #TempTable;\n\n-- Drop the temporary table\nDROP TABLE #TempTable;