SELECT \n    r.`name`, \n    r.`latitude`, \n    r.`longitude`, \n    r.`indoor`, \n    r.`address`, \n    r.`phone`, \n    r.`email`, \n    r.`website`, \n    r.`closed`,\n    GROUP_CONCAT(DISTINCT rt.`type`) AS types\nFROM \n    rocks r\nINNER JOIN \n    rock_types rt ON r.id = rt.id\nWHERE \n    rt.`type` IN ('DWS', 'Top rope')\n    AND (r.`latitude` > -180 AND r.`latitude` < 180)\n    AND (r.`longitude` > -180 AND r.`longitude` < 180)\nGROUP BY \n    r.id