DELIMITER //\nCREATE PROCEDURE sp_SearchData(\n    IN status_id INT,\n    IN date DATE,\n    IN other_parameter VARCHAR(50)\n)\nBEGIN\n    SET @sql = 'SELECT * FROM table WHERE 1=1';\n\n    IF status_id IS NOT NULL THEN\n        SET @sql = CONCAT(@sql, ' AND status_id = ', status_id);\n    END IF;\n\n    IF date IS NOT NULL THEN\n        SET @sql = CONCAT(@sql, ' AND date = ', QUOTE(date));\n    END IF;\n\n    IF other_parameter IS NOT NULL THEN\n        SET @sql = CONCAT(@sql, ' AND other_parameter = ', QUOTE(other_parameter));\n    END IF;\n\n    PREPARE stmt FROM @sql;\n    EXECUTE stmt;\n    DEALLOCATE PREPARE stmt;\nEND;\n//\nDELIMITER ;