CREATE TABLE mytable (\n  A VARCHAR2(10),\n  B VARCHAR2(10),\n  C VARCHAR2(10)\n);\n\nCREATE INDEX myindex ON mytable (A, B, C);\n\n-- Can use the index\nSELECT * FROM mytable WHERE A = 'value';\nSELECT * FROM mytable WHERE A = 'value' AND B = 'value';\nSELECT * FROM mytable WHERE A = 'value' AND B = 'value' AND C = 'value';\n\n-- Cannot use the index\nSELECT * FROM mytable WHERE B = 'value';\nSELECT * FROM mytable WHERE C = 'value';\nSELECT * FROM mytable WHERE B = 'value' AND C = 'value';