-- Create a certificate\nCREATE CERTIFICATE MyApplicationCert\n   ENCRYPTION BY PASSWORD = 'MyCertPassword';\n\n-- Create a login and user for the application\nCREATE LOGIN MyApplicationLogin\n   FROM CERTIFICATE MyApplicationCert;\n\nCREATE USER MyApplicationUser\n   FOR LOGIN MyApplicationLogin;\n\n-- Grant the application role to the application user\nALTER ROLE MyApplicationRole\n   ADD MEMBER MyApplicationUser;