DECLARE @MoreExists BIT;\nDECLARE @TopN INT = 50;\n\n-- Table variable to hold the results\nDECLARE @Results TABLE (\n    Field1 DataType,\n    Field2 DataType,\n    -- ... other fields\n);\n\n-- Insert top N+1 results into the table variable\nINSERT INTO @Results\nSELECT TOP (@TopN + 1)\n    Field1,\n    Field2,\n    -- ... other fields\nFROM\n    MyTable\nWHERE\n    -- some expensive where clause\nORDER BY\n    -- some order by clause if needed;\n\n-- Check if more results exist\nSELECT \n    @MoreExists = CASE WHEN COUNT(*) > @TopN THEN 1 ELSE 0 END\nFROM \n    @Results;\n\n-- Select the top N results\nSELECT TOP (@TopN)\n    Field1,\n    Field2,\n    -- ... other fields\nFROM \n    @Results;\n\n-- @MoreExists now indicates if more results would have been returned.\nSELECT @MoreExists AS MoreExists;