WITH OrderedCases AS (\n  SELECT \n    Case_ID,\n    Name,\n    Date,\n    Status,\n    Type,\n    ROW_NUMBER() OVER (PARTITION BY Case_ID ORDER BY Date) AS RowNumber\n  FROM \n    YourTable\n  WHERE \n    Type = 'Support' AND Status = 'Start'\n)\nSELECT \n  Case_ID,\n  Name,\n  Date,\n  Status,\n  Type\nFROM \n  OrderedCases\nWHERE \n  RowNumber = 1 AND Case_ID IN (\n    SELECT Case_ID FROM CaseTable\n    WHERE Date BETWEEN '2007-11-01' AND '2007-11-30'\n  )