SELECT \n    OrganizationName,\n    [Current],\n    [30-60 days],\n    [60-90 days],\n    [over 90 days],\n    Total = [Current] + [30-60 days] + [60-90 days] + [over 90 days]\nFROM \n    (SELECT \n         OrganizationName,\n         ReceivableStatus,\n         InvoiceFee\n     FROM \n         v_InvoicesFreelanceOutstanding) AS SourceTable\nPIVOT \n    (SUM(InvoiceFee) FOR ReceivableStatus IN ([Current], [30-60 days], [60-90 days], [over 90 days])) AS PivotTable;