SELECT DISTINCT Source.ItemName,\n    COALESCE(Count(SELECT Source.DateAdded FROM Source WHERE Int(Date()-Source.DateAdded) > 20), 0) AS Total,\n    COALESCE(Count(SELECT Source.DateAdded FROM Source WHERE Int(Date()-Source.DateAdded) BETWEEN 20 AND 44), 0) AS BTWN_20_44,\n    COALESCE(Count(SELECT Source.DateAdded FROM Source WHERE Int(Date()-Source.DateAdded) BETWEEN 45 AND 60), 0) AS BTWN_45_60,\n    COALESCE(Count(SELECT Source.DateAdded FROM Source WHERE Int(Date()-Source.DateAdded) BETWEEN 61 AND 90), 0) AS BTWN_61_90,\n    COALESCE(Count(SELECT Source.DateAdded FROM Source WHERE Int(Date()-Source.DateAdded) BETWEEN 91 AND 180), 0) AS BTWN_91_180,\n    COALESCE(Count(SELECT Source.DateAdded FROM Source WHERE Int(Date()-Source.DateAdded) > 180), 0) AS GT_180\nFROM Source\nGROUP BY Source.ItemName;