CREATE TABLE JobPostings (\n    ID INT PRIMARY KEY,\n    UserID INT,\n    Company NVARCHAR(50),\n    JobTitle NVARCHAR(100),\n    JobTypeID INT,\n    JobLocationID INT,\n    JobDescription NVARCHAR(MAX),\n    HowToApply NVARCHAR(MAX),\n    CompanyURL NVARCHAR(100),\n    LogoURL NVARCHAR(100),\n    CreatedAt DATETIME DEFAULT GETDATE(),\n    UpdatedAt DATETIME DEFAULT GETDATE()\n);\n\nCREATE TABLE JobLocations (\n    ID INT PRIMARY KEY,\n    City NVARCHAR(50),\n    State NVARCHAR(50),\n    Zip NVARCHAR(10)\n);\n\nCREATE TABLE JobTypes (\n    ID INT PRIMARY KEY,\n    Name NVARCHAR(50)\n);