CREATE VIEW my_view AS\n(\n  SELECT \n    metadata.ID,\n    metadata.Field1,\n    metadata.Field2,\n    metadata.Field3,\n    metadata.Field4,\n    attribute1.StrValue AS Attr1,\n    attribute2.StrValue AS Attr2\n  FROM \n    metadata,\n    data attribute1,\n    data attribute2\n  WHERE \n    (\n      metadata.Type = 'TYPE1'\n    )\n    AND \n    (\n      metadata.ID = attribute1.ID AND attribute1.name = 'attr1'\n    )\n    AND \n    (\n      metadata.ID = attribute2.ID AND attribute2.name = 'attr2'\n    )\n)