CREATE TABLE Permissions (\n  PermissionID INT PRIMARY KEY,\n  ApplicationID INT NOT NULL,\n  AttributeID INT NOT NULL,\n  AttributeValueID INT NOT NULL,\n  FOREIGN KEY (ApplicationID) REFERENCES Applications(ApplicationID),\n  FOREIGN KEY (AttributeID) REFERENCES Attributes(AttributeID),\n  FOREIGN KEY (AttributeValueID) REFERENCES AttributeValues(AttributeValueID)\n);\n\nINSERT INTO Permissions (PermissionID, ApplicationID, AttributeID, AttributeValueID)\nVALUES (1, 1, 1, 1),  -- Sales department has access to App 1\n       (2, 1, 2, 3),  -- Manager role has access to App 1\n       (3, 1, 3, 4);  -- New York location has access to App 1