CREATE TRIGGER trg_PreventDelete\nON YourTable\nINSTEAD OF DELETE\nAS\nBEGIN\n    -- Your code to execute before delete goes here\n    -- Use the 'deleted' table to access the rows being deleted\n    SELECT * FROM deleted;\n\n    -- If you want to proceed with the delete, you need to explicitly delete the rows\n    DELETE FROM YourTable\n    WHERE YourPrimaryKey IN (SELECT YourPrimaryKey FROM deleted);\nEND;