SELECT p.*\nFROM Parent p\nJOIN ParentChild pc ON p.ParentID = pc.ParentID\nJOIN Child c ON pc.ChildID = c.ChildID\nWHERE c.ChildID = 'Charlie'\nINTERSECT\nSELECT p.*\nFROM Parent p\nJOIN ParentChild pc ON p.ParentID = pc.ParentID\nJOIN Child c ON pc.ChildID = c.ChildID\nWHERE c.ChildID = 'David'\nINTERSECT\nSELECT p.*\nFROM Parent p\nJOIN ParentChild pc ON p.ParentID = pc.ParentID\nJOIN Child c ON pc.ChildID = c.ChildID\nWHERE c.ChildID = 'Eve'