SELECT \n    d.clientid,\n    d.delivery_count,\n    r.action_count\nFROM \n    (SELECT \n         clientid, \n         COUNT(*) AS delivery_count \n     FROM \n         deliveries \n     GROUP BY \n         clientid) d\nINNER JOIN \n    (SELECT \n         clientid, \n         COUNT(*) AS action_count \n     FROM \n         routeactions \n     GROUP BY \n         clientid) r\nON \n    d.clientid = r.clientid;