CREATE TABLE Fact_Experiments (\n  Experiment_ID INT,\n  Velocity DECIMAL(10, 2),  -- continuous range\n  Measure1 DECIMAL(10, 2),\n  Measure2 DECIMAL(10, 2)\n);\n\nCREATE TABLE Dim_Velocity (\n  Velocity_Bin VARCHAR(20),\n  Min_Velocity DECIMAL(10, 2),\n  Max_Velocity DECIMAL(10, 2)\n);\n\nINSERT INTO Dim_Velocity (Velocity_Bin, Min_Velocity, Max_Velocity)\nVALUES\n  ('Low', 0.00, 5.00),\n  ('Medium', 5.01, 10.00),\n  ('High', 10.01, 20.00);\n\nCREATE TABLE Fact_Experiments_Aggregated (\n  Velocity_Bin VARCHAR(20),\n  Measure1 DECIMAL(10, 2),\n  Measure2 DECIMAL(10, 2)\n);\n\nINSERT INTO Fact_Experiments_Aggregated (Velocity_Bin, Measure1, Measure2)\nSELECT \n  dv.Velocity_Bin,\n  SUM(fe.Measure1),\n  SUM(fe.Measure2)\nFROM \n  Fact_Experiments fe\n  JOIN Dim_Velocity dv ON fe.Velocity BETWEEN dv.Min_Velocity AND dv.Max_Velocity\nGROUP BY \n  dv.Velocity_Bin;