-- create the foreign data wrapper\nCREATE EXTENSION IF NOT EXISTS postgres_fdw;\n\n-- create a foreign server\nCREATE SERVER databaseB_server FOREIGN DATA WRAPPER postgres_fdw OPTIONS (\n    host 'localhost', \n    port 5432, \n    dbname 'databaseB'\n);\n\n-- create a foreign table\nCREATE FOREIGN TABLE databaseB_someTableName (\n    ...\n) SERVER databaseB_server OPTIONS (\n    schema_name 'public', \n    table_name 'someTableName'\n);\n\n-- perform the query\nSELECT * FROM databaseB_someTableName;