-- create a connection to the other database\nCREATE EXTENSION IF NOT EXISTS dblink;\n\n-- create a dblink connection to the other database\nSELECT dblink_connect('databaseB', 'dbname=databaseB user=your_username password=your_password');\n\n-- perform the query\nSELECT * FROM dblink('databaseB', 'SELECT * FROM public.someTableName') AS t1 (...);\n\n-- disconnect\nSELECT dblink_disconnect('databaseB');