SELECT \n  EXTRACT(YEAR FROM DATE_TRUNC('week', wid_date - INTERVAL '1 hour')) AS production_year,\n  EXTRACT(WEEK FROM DATE_TRUNC('week', wid_date - INTERVAL '1 hour')) AS production_week,\n  SUM(tonnes)\nFROM \n  production\nWHERE \n  EXTRACT(YEAR FROM DATE_TRUNC('week', wid_date - INTERVAL '1 hour')) = 2009\nGROUP BY \n  EXTRACT(YEAR FROM DATE_TRUNC('week', wid_date - INTERVAL '1 hour')),\n  EXTRACT(WEEK FROM DATE_TRUNC('week', wid_date - INTERVAL '1 hour'))